<?phpclass App_Controller_Plugin extends Zend_Controller_Plugin_Abstract{    public function preDispatch(Zend_Controller_Request_Abstract $request)    {		// GET MODULE/CONTROLLER/ACTION		$module			= $request->getModuleName();		$controller		= $request->getControllerName();		$action			= $request->getActionName();		// SEND MODULE/CONTROLLER/ACTION		$layout	= Zend_Layout::getMvcInstance();		$layout->getView()->module			= $module;		$layout->getView()->controller                  = $controller;		$layout->getView()->action			= $action;		// SET LAYOUT		$layout->getView()->headTitle('Zend', 'SET');		$layout->getView()->headMeta()->appendHttpEquiv('Content-Type', 'text/html; charset=UTF-8');		$layout->getView()->headLink()->appendStylesheet('/css/style.css');                $layout->getView()->headLink()->appendStylesheet('/css/css/customMessages.css');                $layout->getView()->headLink()->appendStylesheet('/css/css/template.css');                $layout->getView()->headLink()->appendStylesheet('/css/css/validationEngine.jquery.css');                $layout->getView()->headLink()->appendStylesheet('/css/jquery-ui-1.10.4.custom.css');				$layout->getView()->headScript()->prependFile('//ajax.googleapis.com/ajax/libs/jquery/1.10.2/jquery.min.js');                $layout->getView()->headScript()->appendFile('/js/js/jquery-1.11.0.min.js');                $layout->getView()->headScript()->appendFile('/js/js/jquery.validationEngine.js');                $layout->getView()->headScript()->appendFile('/js/js/languages/jquery.validationEngine-en.js');                $layout->getView()->headScript()->appendFile('/js/js/contrib/other-validations.js');                $layout->getView()->headScript()->appendFile('/js/jquery-ui-1.10.4.custom.js');                $layout->getView()->headScript()->appendFile('/js/js.js');			//->appendFile('/javascript/jquery.validate.min.js');                                if($controller == 'index'){                    $layout->setLayout('layout');                }elseif($controller =='catalog') {                    $layout->setLayout('layout_catalog');                    }elseif($controller=='user'){                    $layout->setLayout('layout_reg');                 }elseif($controller =='cos') {                    $layout->setLayout('layout_catalog');                    }                                      	}}